package com.example.demo.entities;

import javax.persistence.Entity;
import javax.persistence.Id;
import javax.persistence.Table;

@Entity
@Table(name= "user")
public class User {
	
	@Id
	private int id;
	
	public User() {
		// TODO Auto-generated constructor stub
	}
	
	public User(int id) {
		this.id = id;
	}
	
	public int getId() {
		return this.id;
	}

}
